import React, { useState, useEffect } from 'react';
import { subscribeToFund, sendNotification, fetchFunds, fetchUserByDocument } from '../../services/api';
import './FundSubscription.styles.css';

/**
 * Componente para suscribirse a un fondo.
 * Permite al usuario ingresar detalles del fondo y su método de notificación.
 */
const FundSubscription: React.FC = () => {
  const [formData, setFormData] = useState({ document: '', fundId: '', amount: '' });
  const [userId, setUserId] = useState<string | null>(null);
  const [funds, setFunds] = useState<{ id: string; name: string }[]>([]);
  
  useEffect(() => {
    // Fetch funds when the component mounts
    const loadFunds = async () => {
      const fetchedFunds = await fetchFunds();
      setFunds(fetchedFunds);
    };
    
    loadFunds();
  }, []);

  /**
   * Maneja los cambios en el formulario.
   * @param e Evento de cambio
   */
  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  /**
   * Busca el usuario por documento.
   */
  const handleDocumentSearch = async () => {
    if (formData.document) {
      try {
        const user = await fetchUserByDocument(formData.document);
        setUserId(user.id); // Asumimos que la respuesta tiene un campo 'id'
      } catch (error) {
        console.error('Error fetching user', error);
        alert('User not found. Please check the document number.');
      }
    }
  };

  /**
   * Maneja el envío del formulario.
   * Suscribe al usuario al fondo y envía una notificación.
   * @param e Evento de envío
   */
  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    try {
      const subscriptionData = {
        userId,
        fundId: formData.fundId,
        transactionType: 'subscribe',
        amount: Number(formData.amount),
        active: true,
      };

      await subscribeToFund(subscriptionData);
      await sendNotification('You have subscribed to the fund.');
      alert('Subscription successful!');
    } catch (error) {
      console.error('Error subscribing to fund', error);
      alert('Subscription failed. Please try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="fund-subscription-form">
      <h2>Subscribe to a Fund</h2>
      
      <input
        type="text"
        name="document"
        placeholder="User Document"
        onChange={handleChange}
        onBlur={handleDocumentSearch} // Buscar usuario al salir del campo
        required
      />
      
      {userId && <p>User ID: {userId}</p>} {/* Mostrar ID del usuario */}

      <select name="fundId" onChange={handleChange} required>
        <option value="">Select Fund</option>
        {funds.map(fund => (
          <option key={fund.id} value={fund.id}>{fund.name}</option>
        ))}
      </select>

      <input
        type="number"
        name="amount"
        placeholder="Subscription Amount"
        onChange={handleChange}
        required
      />
      
      <button type="submit">Subscribe</button>
    </form>
  );
};

export default FundSubscription;
