import React, { useEffect, useState } from 'react';
import { subscribeToFund, fetchUserByDocument, fetchFunds } from '../../services/api';
import './FundSubscription.styles.css';
import { Toaster, toast } from 'sonner'; // Importa Sonner

const FundSubscription: React.FC = () => {
  const [formData, setFormData] = useState({
    document: '',
    fundId: '',
    amount: 0,
    notifyBy: 'email'
  });
  const [userId, setUserId] = useState<string | null>(null);
  const [funds, setFunds] = useState<{ id: string; name: string }[]>([]);
  const [isUserValid, setIsUserValid] = useState<boolean>(false);
  const [userList, setUserList] = useState<{ id: string; name: string }[]>([]);
  const [displayedUsers, setDisplayedUsers] = useState<{ id: string; name: string }[]>([]);
  const [filterText, setFilterText] = useState('');
  const [start, setStart] = useState(0);
  const [limit] = useState(5); // Mostrar siempre 5 usuarios

  useEffect(() => {
    const loadFunds = async () => {
      try {
        const fundsList = await fetchFunds();
        setFunds(fundsList);
      } catch (error) {
        // Mensaje de error cuando hay un problema al cargar los fondos
        toast.error('Error fetching funds. Please try again later.');
      }
    };

    loadFunds();
  }, []);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    if (e.target.name === 'document') {
      setFilterText(e.target.value);
      handleFetchUser();
    }
  };

  const handleFetchUser = async () => {
    if (formData.document) {
      try {
        const user = await fetchUserByDocument(formData.document);
        setUserId(user.id);
        setIsUserValid(true);
        setUserList([user]); // Aqu√≠ puedes almacenar la lista completa de usuarios
        handleFilterUsers(); // Llama a la funci√≥n para filtrar usuarios
        // Notificaci√≥n de √©xito
        toast.success('User found!');
      } catch (error) {
        // Notificaci√≥n de error si no se encuentra el usuario
        toast.error('User not found. Please check the document.', {
          duration: 5000, // Duraci√≥n m√°s larga
          icon: 'üö´', // A√±adir un icono para que sea m√°s llamativo
        });
        setIsUserValid(false);
      }
    }
  };

  const handleFilterUsers = () => {
    const filteredUsers = userList.filter(user =>
      user.name.toLowerCase().includes(filterText.toLowerCase())
    );
    setDisplayedUsers(filteredUsers.slice(start, start + limit));
  };

  const loadMoreUsers = () => {
    setStart(prevStart => prevStart + limit);
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (!userId) {
      // Mostrar notificaci√≥n de error si no se valida el usuario
      toast.error('Please validate the user document first.', {
        icon: '‚ö†Ô∏è', // Icono para destacar el error
      });
      return;
    }

    try {
      const subscriptionData = {
        userId,
        fundId: formData.fundId,
        document: formData.document,
        transactionType: 'subscribe',
        amount: Number(formData.amount),
        active: true,
      };

      const response = await subscribeToFund(subscriptionData);

      const successMessage = typeof response === 'object' && 'message' in response 
        ? response.message 
        : 'Subscription successful!'; 

      // Notificaci√≥n de √©xito
      toast.success(successMessage, {
        duration: 4000,
        icon: '‚úÖ', // A√±adir un icono de √©xito
      });

      // Restablecer los campos del formulario despu√©s del √©xito
      setFormData({
        document: '',
        fundId: '',
        amount: 0,
        notifyBy: 'email'
      });
      setUserId(null);
      setIsUserValid(false);
    } catch (error: any) {
      const errorMessage = error.response?.data?.message || 'Subscription failed. Please try again.';
      // Notificaci√≥n de error si la suscripci√≥n falla
      toast.error(errorMessage, {
        duration: 5000, // Duraci√≥n m√°s larga para errores
        icon: '‚ùå', // Icono para errores
      });
    }
  };

  const isFormValid = () => {
    return formData.fundId !== '' && formData.amount > 0 && isUserValid;
  };

  return (
    <div>
      <form onSubmit={handleSubmit} className="fund-subscription-form">
        <h2>Subscribe to a Fund</h2>

        <input
          type="text"
          name="document"
          placeholder="Document"
          value={formData.document}
          onChange={handleChange}
          required
        />
        <button type="button" onClick={handleFetchUser} className="fetch-user-button">
          Validate Document
        </button>

        <div className="user-list-container" style={{ maxHeight: '150px', overflowY: 'scroll' }}>
          {displayedUsers.map(user => (
            <div key={user.id} className="user-item" onClick={() => setFormData({ ...formData, document: user.name })}>
              {user.name}
            </div>
          ))}
          {filterText && displayedUsers.length === 0 && <div>No users found</div>}
        </div>

        <select 
          name="fundId" 
          value={formData.fundId}
          onChange={handleChange} 
          disabled={!isUserValid} 
          required
        >
          <option value="">Select a Fund</option>
          {funds.map(fund => (
            <option key={fund.id} value={fund.id}>
              {fund.name}
            </option>
          ))}
        </select>
        
        <input
          type="number"
          name="amount"
          value={formData.amount}
          placeholder="Amount"
          onChange={handleChange}
          disabled={!isUserValid} 
          required
        />

        <select 
          name="notifyBy" 
          value={formData.notifyBy}
          onChange={handleChange} 
          disabled={!isUserValid}
        >
          <option value="email">Email</option>
          <option value="sms">SMS</option>
        </select>

        <button 
          type="submit" 
          className={`subscribe-button ${!isFormValid() ? 'inactive' : ''}`} 
          disabled={!isFormValid()}
        >
          Subscribe
        </button>
      </form>

      {/* Contenedor de notificaciones con posici√≥n superior */}
      <Toaster position="top-center" />
    </div>
  );
};

export default FundSubscription;
