import React, { useEffect, useState } from 'react';
import { subscribeToFund, sendNotification, fetchUserByDocument, fetchFunds } from '../../services/api';
import './FundSubscription.styles.css';

const FundSubscription: React.FC = () => {
  const [formData, setFormData] = useState({
    document: '',
    fundId: '',
    amount: 0,
    notifyBy: 'email'
  });
  const [userId, setUserId] = useState<string | null>(null); // Estado para almacenar el ID del usuario
  const [funds, setFunds] = useState<{ id: string; name: string }[]>([]); // Estado para almacenar los fondos
  const [isUserValid, setIsUserValid] = useState<boolean>(false); // Estado para controlar la validez del usuario

  useEffect(() => {
    const loadFunds = async () => {
      try {
        const fundsList = await fetchFunds(); // Llamada al servicio para obtener los fondos
        setFunds(fundsList);
      } catch (error) {
        console.error('Error fetching funds', error);
        alert('Could not fetch funds. Please try again later.');
      }
    };

    loadFunds();
  }, []);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFetchUser = async () => {
    if (formData.document) {
      try {
        const user = await fetchUserByDocument(formData.document);
        setUserId(user.id);
        setIsUserValid(true); // Establecer que el usuario es válido
        alert('User found!'); // Notificación de éxito
      } catch (error) {
        console.error('Error fetching user', error);
        alert('User not found. Please check the document.');
        setIsUserValid(false); // Establecer que el usuario no es válido
      }
    }
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (!userId) {
      alert('Please validate the user document first.');
      return;
    }
    try {
      const subscriptionData = {
        userId,
        fundId: formData.fundId,
        transactionType: 'subscribe',
        amount: formData.amount,
        active: true,
      };

      await subscribeToFund(subscriptionData);
      await sendNotification('You have subscribed to the fund.', formData.notifyBy);
      alert('Subscription successful!');
    } catch (error) {
      console.error('Error subscribing to fund', error);
      alert('Subscription failed. Please try again.');
    }
  };

  // Verificación para habilitar el botón de suscripción
  const isFormValid = () => {
    return isUserValid && formData.fundId && formData.amount > 0;
  };

  return (
    <form onSubmit={handleSubmit} className="fund-subscription-form">
      <h2>Subscribe to a Fund</h2>

      {/* Campo para el documento */}
      <input
        type="text"
        name="document"
        placeholder="Document"
        onChange={handleChange}
        required
      />
      <button type="button" onClick={handleFetchUser} className="fetch-user-button">
        Validate Document
      </button>

      {/* Selección de fondos desde la lista desplegable */}
      <select 
        name="fundId" 
        onChange={handleChange} 
        disabled={!isUserValid} // Habilitar solo si el usuario es válido
        required
      >
        <option value="">Select a Fund</option>
        {funds.map(fund => (
          <option key={fund.id} value={fund.id}>
            {fund.name}
          </option>
        ))}
      </select>
      
      {/* Campo para el monto */}
      <input
        type="number"
        name="amount"
        placeholder="Amount"
        onChange={handleChange}
        disabled={!isUserValid} // Habilitar solo si el usuario es válido
        required
      />

      {/* Método de notificación */}
      <select name="notifyBy" onChange={handleChange} disabled={!isUserValid}>
        <option value="email">Email</option>
        <option value="sms">SMS</option>
      </select>

      {/* Botón de suscripción habilitado solo si el formulario es válido */}
      <button type="submit" disabled={!isFormValid()}>Subscribe</button>
    </form>
  );
};

export default FundSubscription;
