import React, { useState } from 'react';
import { cancelFundSubscription, sendNotification } from '../../services/api';
import './FundCancellation.styles.css';

/**
 * Componente para cancelar la suscripción a un fondo.
 * Permite al usuario ingresar detalles del fondo que desea cancelar.
 */
const FundCancellation: React.FC = () => {
  const [formData, setFormData] = useState({ name: '', email: '' });

  /**
   * Maneja los cambios en el formulario.
   * @param e Evento de cambio
   */
  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  /**
   * Maneja el envío del formulario.
   * Cancela la suscripción del usuario y envía una notificación.
   * @param e Evento de envío
   */
  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    try {
      await cancelFundSubscription(formData.name);
      await sendNotification('You have cancelled your subscription.' );
      alert('Cancellation successful!');
    } catch (error) {
      console.error('Error cancelling subscription', error);
      alert('Cancellation failed. Please try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="fund-cancellation-form">
      <h2>Cancel Fund Subscription</h2>
      <input
        type="text"
        name="name"
        placeholder="Fund Name"
        onChange={handleChange}
        required
      />
      <input
        type="email"
        name="email"
        placeholder="Email"
        onChange={handleChange}
        required
      />
      <button type="submit">Cancel Subscription</button>
    </form>
  );
};

export default FundCancellation;
