import React, { useEffect, useState } from 'react';
import { subscribeToFund, fetchUserByDocument, fetchFunds } from '../../services/api';
import './FundSubscription.styles.css';
import { Toaster, toast } from 'sonner';

interface User {
  id: string;
  name: string;
  document: string;
}

const FundSubscription: React.FC = () => {
  const [formData, setFormData] = useState({
    document: '',
    fundId: '',
    amount: 0,
    notifyBy: 'email'
  });
  const [userId, setUserId] = useState<string | null>(null);
  const [funds, setFunds] = useState<{ id: string; name: string }[]>([]);
  const [users, setUsers] = useState<User[]>([]);
  const [filteredUsers, setFilteredUsers] = useState<User[]>([]);
  const [isUserValid, setIsUserValid] = useState<boolean>(false);

  // Cargar fondos al montar el componente
  useEffect(() => {
    const loadFunds = async () => {
      try {
        const fundsList = await fetchFunds();
        setFunds(fundsList);
      } catch (error) {
        toast.error('Error fetching funds. Please try again later.');
      }
    };

    loadFunds();
  }, []);

  // Cargar usuarios al montar el componente
  useEffect(() => {
    const loadUsers = async () => {
      try {
        const usersList = await fetchUserByDocument();
        setUsers(usersList);
        setFilteredUsers(usersList); // Inicialmente, todos los usuarios están filtrados
      } catch (error) {
        toast.error('Error fetching users. Please try again later.');
      }
    };

    loadUsers();
  }, []);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    if (e.target.name === 'document') {
      handleValidateUser();
    }
  };

  const handleValidateUser = () => {
    const user = users.find(user => user.document === formData.document);
    if (user) {
      setUserId(user.id);
      setIsUserValid(true);
    } else {
      setUserId(null);
      setIsUserValid(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (!userId) {
      toast.error('Please validate the user document first.', {
        icon: '⚠️',
      });
      return;
    }

    try {
      const subscriptionData = {
        userId,
        fundId: formData.fundId,
        document: formData.document,
        transactionType: 'subscribe',
        amount: Number(formData.amount),
        active: true,
      };

      const response = await subscribeToFund(subscriptionData);
      const successMessage = typeof response === 'object' && 'message' in response 
        ? response.message 
        : 'Subscription successful!'; 

      toast.success(successMessage, {
        duration: 4000,
        icon: '✅',
      });

      setFormData({
        document: '',
        fundId: '',
        amount: 0,
        notifyBy: 'email'
      });
      setUserId(null);
      setIsUserValid(false);
    } catch (error: any) {
      const errorMessage = error.response?.data?.message || 'Subscription failed. Please try again.';
      toast.error(errorMessage, {
        duration: 5000,
        icon: '❌',
      });
    }
  };

  const isFormValid = () => {
    return formData.fundId !== '' && formData.amount > 0 && isUserValid;
  };

  // Filtrar usuarios
  const handleUserFilter = (event: React.ChangeEvent<HTMLInputElement>) => {
    const value = event.target.value.toLowerCase();
    const filtered = users.filter(user =>
      user.name.toLowerCase().includes(value) || user.document.includes(value)
    );
    setFilteredUsers(filtered);
  };

  const handleUserSelect = (user: User) => {
    setFormData({ ...formData, document: user.document }); // Llena el documento
    setUserId(user.id); // Guarda el ID del usuario seleccionado
    setIsUserValid(true); // Activa los demás campos
    setFilteredUsers(users); // Resetea la lista filtrada
  };

  return (
    <div>
      <form onSubmit={handleSubmit} className="fund-subscription-form">
        <h2>Subscribe to a Fund</h2>

        <input
          type="text"
          name="document"
          placeholder="Document"
          value={formData.document}
          onChange={handleChange}
          required
        />

        {/* Campo de entrada para filtrar usuarios */}
        <input
          type="text"
          placeholder="Search User"
          onChange={handleUserFilter}
        />

        {/* Lista de usuarios filtrados */}
        {filteredUsers.length > 0 && (
          <ul className="user-list">
            {filteredUsers.map(user => (
              <li key={user.id} onClick={() => handleUserSelect(user)}>
                {user.name} - {user.document}
              </li>
            ))}
          </ul>
        )}

        <input
          type="text"
          placeholder="Search Fund"
          onChange={(e) => {
            const value = e.target.value.toLowerCase();
            const filtered = funds.filter(fund =>
              fund.name.toLowerCase().includes(value)
            );
            setFunds(filtered);
          }}
        />

        <select 
          name="fundId" 
          value={formData.fundId}
          onChange={handleChange} 
          disabled={!isUserValid} 
          required
        >
          <option value="">Select a Fund</option>
          {funds.map(fund => (
            <option key={fund.id} value={fund.id}>
              {fund.name}
            </option>
          ))}
        </select>
        
        <input
          type="number"
          name="amount"
          value={formData.amount}
          placeholder="Amount"
          onChange={handleChange}
          disabled={!isUserValid} 
          required
        />

        <select 
          name="notifyBy" 
          value={formData.notifyBy}
          onChange={handleChange} 
          disabled={!isUserValid}
        >
          <option value="email">Email</option>
          <option value="sms">SMS</option>
        </select>

        <button 
          type="submit" 
          className={`subscribe-button ${!isFormValid() ? 'inactive' : ''}`} 
          disabled={!isFormValid()}
        >
          Subscribe
        </button>
      </form>

      <Toaster position="top-center" />
    </div>
  );
};

export default FundSubscription;
