import React, { useState } from 'react';
import { cancelFundSubscription, sendNotification } from '../../services/api';
import './FundCancellation.styles.css';

const FundCancellation: React.FC = () => {
  const [formData, setFormData] = useState({ name: '', notifyBy: 'email' });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    try {
      await cancelFundSubscription(formData.name);

      // Enviar notificación utilizando el método seleccionado
      await sendNotification('You have cancelled your subscription.', formData.notifyBy);
      
      alert('Cancellation successful!');
    } catch (error) {
      console.error('Error cancelling subscription', error);
      alert('Cancellation failed. Please try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="fund-cancellation-form">
      <h2>Cancel Fund Subscription</h2>
      <input
        type="text"
        name="name"
        placeholder="Fund Name"
        onChange={handleChange}
        required
      />
      <select name="notifyBy" onChange={handleChange}>
        <option value="email">Email</option>
        <option value="sms">SMS</option>
      </select>
      <button type="submit">Cancel Subscription</button>
    </form>
  );
};

export default FundCancellation;
