import React, { useEffect, useState } from 'react';
import { subscribeToFund, fetchUserByDocument, fetchFunds } from '../../services/api';
import './FundSubscription.styles.css';
import { Toaster, toast } from 'sonner';

interface User {
  id: string;
  name: string;
  documento: string;
}

const FundSubscription: React.FC = () => {
  const [formData, setFormData] = useState({
    document: '',
    fundId: '',
    amount: 0,
    notifyBy: 'email'
  });
  const [userId, setUserId] = useState<string | null>(null);
  const [funds, setFunds] = useState<{ id: string; name: string }[]>([]);
  const [users, setUsers] = useState<User[]>([]);
  const [displayedUsers, setDisplayedUsers] = useState<User[]>([]);
  const [isUserValid, setIsUserValid] = useState<boolean>(false);
  const [filterText, setFilterText] = useState('');
  const [start, setStart] = useState(0);
  const [limit] = useState(5); // Mostrar siempre 5 usuarios

  // Cargar fondos al montar el componente
  useEffect(() => {
    const loadFunds = async () => {
      try {
        const fundsList = await fetchFunds();
        setFunds(fundsList);
      } catch (error) {
        toast.error('Error fetching funds. Please try again later.');
      }
    };

    loadFunds();
  }, []);

  // Cargar usuarios al montar el componente
  useEffect(() => {
    const loadUsers = async () => {
      try {
        const usersList = await fetchUserByDocument();
        setUsers(usersList);
        setDisplayedUsers(usersList.slice(start, start + limit));
      } catch (error) {
        toast.error('Error fetching users. Please try again later.');
      }
    };

    loadUsers();
  }, []);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    if (e.target.name === 'document') {
      setFilterText(e.target.value);
      handleFilterUsers();
    }
  };

  const handleFilterUsers = () => {
    const filteredUsers = users.filter(user =>
      user.name.toLowerCase().includes(filterText.toLowerCase())
    );
    setDisplayedUsers(filteredUsers.slice(start, start + limit));
  };

  const handleUserSelect = (user: User) => {
    console.log(user.documento, "documentttttttt");
    setFormData({ ...formData, document: user.documento }); // Aquí se llena el documento
    setUserId(user.id); // Guarda el ID del usuario seleccionado
    setIsUserValid(true); // Activa los demás campos
  };

  const loadMoreUsers = () => {
    setStart(prevStart => prevStart + limit);
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (!userId) {
      toast.error('Please validate the user document first.', {
        icon: '⚠️',
      });
      return;
    }
    const subscriptionData = {
      userId,
      fundId: formData.fundId,
      document: formData.document,
      transactionType: 'subscribe',
      amount: Number(formData.amount),
      active: true,
    };
    try {
      

      const response = await subscribeToFund(subscriptionData);

      // Verificar el contenido de la respuesta
      console.log('Backend response:', response);

      const successMessage = typeof response === 'object' && 'message' in response
        ? response.message
        : 'Subscription successful!';

      toast.success(successMessage, {
        duration: 4000,
        icon: '✅',
      });

      // Restablecer el formulario y otros estados
      setFormData({
        document: '',
        fundId: '',
        amount: 0,
        notifyBy: 'email'
      });
      setUserId(null);
      setIsUserValid(false);
      setFilterText(''); // Limpiar el texto del filtro
      setDisplayedUsers(users.slice(start, start + limit)); // Restablecer la lista de usuarios mostrados
      setStart(0); // Reiniciar la paginación de usuarios
    } catch (error: any) {
      console.error('Error:', error); // Verificar el objeto de error completo
      const response = await subscribeToFund(subscriptionData);
        toast.error(String(response), {
        duration: 5000,
        icon: '❌',
      });
      setFormData({
        document: '',
        fundId: '',
        amount: 0,
        notifyBy: 'email'
      });
      setUserId(null);
      setIsUserValid(false);
      setFilterText(''); // Limpiar el texto del filtro
      setDisplayedUsers(users.slice(start, start + limit)); // Restablecer la lista de usuarios mostrados
      setStart(0); // Reiniciar la paginación de usuarios
    }
  };

  const isFormValid = () => {
    return formData.fundId !== '' && formData.amount > 0 && isUserValid;
  };

  return (
    <div>
      <form onSubmit={handleSubmit} className="fund-subscription-form">
        <h2>Subscribe to a Fund</h2>
        
        <select
          name="userId"
          value={userId || ''} // Establecer el valor actual del select
          onChange={(e) => {
            const selectedUser = users.find(user => user.id === e.target.value);
            console.log(selectedUser, "selectedUserqqqqq");
            if (selectedUser) {
              handleUserSelect(selectedUser); // Llamar a la función para manejar la selección
            } else {
              setUserId(null); // Limpiar el ID del usuario si no se selecciona ninguno
              setFormData({ ...formData, document: '' }); // Limpiar el documento si no hay usuario seleccionado
              setIsUserValid(false); // Desactivar los demás campos si no hay usuario
            }
          }}
          required
        >
          <option value="">Select a User</option>
          {displayedUsers.map(user => (
            <option key={user.id} value={user.id}>
              {user.name || 'Nombre no disponible'}
            </option>
          ))}
          {filterText && displayedUsers.length === 0 && <option disabled>No users found</option>}
        </select>

        <select
          name="fundId"
          value={formData.fundId}
          onChange={handleChange}
          disabled={!isUserValid}
          required
        >
          <option value="">Select a Fund</option>
          {funds.map(fund => (
            <option key={fund.id} value={fund.id}>
              {fund.name}
            </option>
          ))}
        </select>

        <input
          type="number"
          name="amount"
          value={formData.amount}
          placeholder="Amount"
          onChange={handleChange}
          disabled={!isUserValid}
          required
        />

        <select
          name="notifyBy"
          value={formData.notifyBy}
          onChange={handleChange}
          disabled={!isUserValid}
        >
          <option value="email">Email</option>
          <option value="sms">SMS</option>
        </select>

        <button
          type="submit"
          className={`subscribe-button ${!isFormValid() ? 'inactive' : ''}`}
          disabled={!isFormValid()}
        >
          Subscribe
        </button>
      </form>

      <Toaster position="top-center" />
    </div>
  );
};

export default FundSubscription;
