import React, { useState } from 'react';
import { cancelFundSubscription, sendNotification } from '../../services/api';
import './FundCancellation.styles.css';


/**
 * Define la estructura del tipo de las transacciones.
 */
interface Cancellation {
  userId: string;
  fundId: string; 
}
const FundCancellation: React.FC = () => {
  const [cancellations, setCancellations] = useState<Cancellation[]>([]);
  const [userId, setUserId] = useState<string>(''); // Para almacenar el número de documento
  const [fundId, setFundId] = useState<string>('');

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setCancellations({ ...cancellations, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    try {
      await cancelFundSubscription(userId, fundId);

      // Enviar notificación utilizando el método seleccionado
      //await sendNotification('You have cancelled your subscription.');
      
      alert('Cancellation successful!');
    } catch (error) {
      console.error('Error cancelling subscription', error);
      alert('Cancellation failed. Please try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="fund-cancellation-form">
      <h2>Cancel Fund Subscription</h2>

      <input
        type="text"
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
        placeholder="Número de documento"
      />
      <input
        type="text"
        name="name"
        placeholder="Fund Name"
        onChange={handleChange}
        required
      />
       
      <button type="submit">Cancel Subscription</button>
    </form>
  );
};

export default FundCancellation;
